import React from 'react';
import './CreatePost.css'
import { useState } from 'react'


const CreatePost = () => {

    const [post, setPost] = useState({name: "", familiar: "", knowledge: "", power: ""})

    const handleChange = (event) => {
        const {name, value} = event.target;
        setPost( (prev) => {
            return {
                ...prev,
                [name]:value,
            }
        })
    }

    return (
        <div>
            <form>
                <label for="name">Name</label> <br />
                <input type="text" id="name" name="name" onChange={handleChange} /><br />
                <br/>

                <label for="author">familiar</label><br />
                <input type="text" id="familiar" name="familiar" onChange={handleChange} /><br />
                <br/>

                <label for="description">knowledge</label><br />
                <textarea rows="5" cols="50" id="description" onChange={handleChange}>
                </textarea>
                <br/>
                <input type="submit" value="Submit" />
            </form>
        </div>
    )
}

export default CreatePost
